import React, {FC, useEffect} from 'react';
import styles from './NavBar.module.css'
import {useTranslation} from "react-i18next";

const NavBar = () => {

    let lang: string | null;

    const {t, i18n} = useTranslation();
    lang = localStorage.getItem("i18nextLng")
    const changeLanguage = () => {
        lang === "en" ?
            i18n.changeLanguage("en")
            :
            i18n.changeLanguage("ru")
    }

    useEffect(() => {
        lang = localStorage.getItem("i18nextLng")
    }, [changeLanguage])

    return (
        <div className={styles.navbar}>
            <div className={styles.container}>
                <div className={styles.decoration}>{t("bio")}</div>
                <div className={styles.decoration}>{t("petProjects")}</div>
                <div className={styles.decoration}>{t("workHistory")}</div>
                <div onClick={() => changeLanguage()} className={styles.decoration}>{t("language")}: {t("languageExactly")}</div>
            </div>
        </div>
    );
};

export default NavBar;
