import React from 'react';
import styles from './NavBar.module.css'
import {useTranslation} from "react-i18next";
import { useNavigate } from "react-router-dom";
import {BIO_ROUTE, PETPROJ_ROUTE, WORKHISTORY_ROUTE} from "../../utils/constants";

const NavBar = () => {
    const navigate = useNavigate();

    const {t, i18n} = useTranslation();
    const changeLanguage = () => {
        const lang = localStorage.getItem("i18nextLng")
        lang === "en" ?
            i18n.changeLanguage("ru")
            :
            i18n.changeLanguage("en")
    }

    return (
        <div className={styles.navbar}>
            <div className={styles.container}>
                <div onClick={() => navigate(BIO_ROUTE)} className={styles.decoration}>{t("navbar.bio")}</div>
                <div onClick={() => navigate(PETPROJ_ROUTE)} className={styles.decoration}>{t("petProjects")}</div>
                <div onClick={() => navigate(WORKHISTORY_ROUTE)} className={styles.decoration}>{t("workHistory")}</div>
                <div onClick={() => changeLanguage()} className={styles.decoration}>{t("language")}: {t("languageExactly")}</div>
            </div>
        </div>
    );
};

export default NavBar;
