import React, {FC, useEffect} from 'react';
import styles from './NavBar.module.css'
import {useTranslation} from "react-i18next";

const NavBar = () => {

    let lang: string | null;

    useEffect(() => {
        lang = localStorage.getItem("i18nextLng")
        console.log(lang)
    }, [])

    const {t, i18n} = useTranslation();
    const changeLanguage = (language: string) => {
        i18n.changeLanguage(language)
    }

    return (
        <div className={styles.navbar}>
            <div className={styles.container}>
                <div className={styles.decoration}>{t("bio")}</div>
                <div className={styles.decoration}>{t("petProjects")}</div>
                <div className={styles.decoration}>{t("workHistory")}</div>
                <div onClick={() => changeLanguage("ru")} className={styles.decoration}>{t("language")}: {t("languageExactly")}</div>
            </div>
        </div>
    );
};

export default NavBar;
